package com.atb.app.activities;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;import android.os.Handler;import com.atb.app.R;import com.atb.app.base.CommonActivity;import com.atb.app.preference.PrefConst;import com.atb.app.preference.Preference;public class SplashActivity extends CommonActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_splash);        gotoNextpage();    }    void gotoNextpage() {        new Handler().postDelayed(new Runnable() {            @Override            public void run() {                goTo(SplashActivity.this, LoginActivity.class, true);            }        }, 3000);    }}